export type PreloadOption = PreloadRequest[] | (() => Promise<PreloadRequest[]>) | (() => PreloadRequest[]);
export interface BasePreloadRequest {
    /**
     * Additional headers which will be sent with the request
     */
    headers?: {
        [name: string]: string;
    };
}
export interface PathPreloadRequest extends BasePreloadRequest {
    /**
     * Path of the page which will be prerendered
     */
    path: string;
}
export interface TopPreloadRequest extends BasePreloadRequest {
    /**
     * Edgio will choose which pages to prerender based on site traffic.
     * The value means maximum number of pages that should be prerendered based on site traffic.
     */
    top: number;
}
/**
 * Page for prerendering can be selected by path or by site traffic.
 */
export type PreloadRequest = PathPreloadRequest | TopPreloadRequest;
export interface PreloadConfig {
    requests: PreloadRequest[];
    concurrency?: number;
}
