import { HttpStatusCode } from '../types';
/**
 * Options for controlling the behavior of redirect method
 */
export type RedirectOptions = {
    /**
     * Query strings to be added to the redirect url.
     */
    query?: {
        [key: string]: string;
    };
    /**
     * HTTP Response status code for the redirect response
     * Default: 302
     */
    statusCode?: number;
} | number;
/**
 * Translates redirect params into normalized version that solves backward compatibility
 * issues and also takes into account query parameters that may be included in the query
 * string itself.
 *
 * @param to string to where we are redirecting the request; may have query string
 * @param options options with which we are redirecting the request
 */
export declare const normalizeRedirectOptions: (to: string, options: RedirectOptions) => {
    to: string;
    statusCode: HttpStatusCode;
    query: {
        [key: string]: string;
    };
};
