import { HttpStatusCode } from '../types';
/**
 * Options for the `serveStatic` method.
 */
export interface ServeStaticOptions {
    /**
     * Use this value to override the default cache-control value used by serverStatic functions (whis is by default FAR_FUTURE_TTL)
     */
    edgeMaxAgeSeconds?: number;
    /**
     * When true, assets are served from a bucket/prefix that remains available across
     * all builds on a given environment.
     */
    permanent?: boolean;
    /**
     * Defines the rewrite source in serveStatic as this cannot be inferred from the criteria
     * as in case when path criteria is string array (InOperatorValues), we need to decide which
     * source to use for multiple paths.
     * @default '/:path*'
     */
    rewritePathSource?: string;
    /**
     * Sets the status code for the response.
     */
    statusCode?: HttpStatusCode;
}
