import { DirOptions } from './DirOptions';
import { FeatureCreator } from './RouteHelper';
/**
 * Used to configure static serving of files from a directory.
 */
export interface StaticOptions extends Partial<DirOptions> {
    /**
     * Add caching or other additional route handling logic here. The function is passed a ResponseWriter, the same as any other route handler would be.
     */
    handler?: FeatureCreator;
    /**
     * Defines the rewrite source in serveStatic as this cannot be inferred from the criteria
     * as in case when path criteria is string array (InOperatorValues), we need to decide which
     * source to use for multiple paths.
     * @default '/:path*'
     */
    rewritePathSource?: string;
}
