import Phase from '../Phase';
import RequestContext from '../RequestContext';
/**
 * Base class for all Sailfish modules
 */
export default abstract class Mod {
    protected readonly context: RequestContext;
    /**
     * @param context The context for the current request
     */
    constructor(context: RequestContext);
    /**
     * Executes a specific phase of the request flow
     * @param phase
     */
    abstract execute(phase: Phase): Promise<void>;
    /**
     * Returns the class name of the module
     */
    abstract toString(): string;
}
