/**
 * Converts a path with express-style or regex substitution params
 * to a globby path pattern.
 * @param path
 * @example pathToGlobby('/public/:path*') => '/public/**'
 * @example pathToGlobby('/public/:file.png') => '/public/*.png'
 * @example pathToGlobby('/public/$1') => '/public/**'
 */
export default function pathToGlobby(path: string): string;
